mapper.select:
select distinct *
from attraction_info


        content_type_id = #{contentTypeId}


        and sido_code = #{sidoCode}


        sido_code = #{sidoCode}


        and gugun_code = #{gugunCode}


        gugun_code = #{gugunCode}

select distinct *
from attraction_info
order by content_id;

select distinct *
from attraction_info
where title = #{name}

select distinct *
from attraction_info
where content_id = #{contentId}

select sido_name
from sido
where sido_code = #{sidoCode}

select distinct gugun_name
from gugun
where gugun_code = #{gugunCode} and sido_code = #{sidoCode}

select type_name
from content_type
where content_type_id = #{contentTypeId}
